{"command":"cd /Users/allen/0xbigboss/ghostty && python - <<'PY'
import json
from pathlib import Path

crash_dir = Path('/Users/allen/.local/state/ghostty/crash')
files = sorted(crash_dir.glob('*.ghosttycrash'))

hits = []
for path in files:
    with path.open('rb') as f:
        # Read first 3 lines (header, item header, event JSON)
        lines = []
        for _ in range(3):
            line = f.readline()
            if not line:
                break
            lines.append(line)
        if len(lines) < 3:
            continue
        try:
            event = json.loads(lines[2])
        except json.JSONDecodeError:
            continue

    def frames_from_stack(stack):
        if not stack:
            return []
        frames = stack.get('frames') or []
        return frames

    # Search in threads
    found = False
    found_frames = []
    threads = event.get('threads') or {}
    values = threads.get('values') or []
    for t in values:
        stack = t.get('stacktrace')
        frames = frames_from_stack(stack)
        for fr in frames:
            fn = fr.get('function') or ''
            if 'updateFrame' in fn or 'renderer.generic' in fn:
                found = True
        if found:
            found_frames = frames
            break

    # Search in exception stacktrace if no thread match
    if not found:
        exc = event.get('exception') or {}
        for ex in exc.get('values') or []:
            frames = frames_from_stack(ex.get('stacktrace'))
            for fr in frames:
                fn = fr.get('function') or ''
                if 'updateFrame' in fn or 'renderer.generic' in fn:
                    found = True
            if found:
                found_frames = frames
                break

    if found:
        hits.append((path.name, event, found_frames))

print(f"found {len(hits)} crash reports with updateFrame in stack")
for name, event, frames in hits:
    print('\n', name)
    print('timestamp', event.get('timestamp'))
    print('release', event.get('release'))
    print('os', event.get('contexts', {}).get('os', {}))
    # print top few frames (last few are top of stack in sentry?)
    # Sentry frames are usually oldest->newest; print last 12
    for fr in frames[-12:]:
        fn = fr.get('function')
        mod = fr.get('module')
        src = fr.get('filename')
        line = fr.get('lineno')
        print(f"  {mod} {fn} {src}:{line}")
PY"}

