const std = @import("std");

pub fn build(b: *std.Build) void {
    const target = b.standardTargetOptions(.{});
    const optimize = b.standardOptimizeOption(.{});

    // Shared library dependencies
    const lib_deps: ServiceConfig.Libs = .{
        .pq = "pq",
        .hiredis = "hiredis",
        .protobuf_c = "protobuf-c",
    };

    // Gateway service
    const gateway = addService(b, .{
        .name = "gateway",
        .root = "src/services/gateway/main.zig",
        .target = target,
        .optimize = optimize,
        .libs = lib_deps,
    });

    // Screening service
    const screening = addService(b, .{
        .name = "screening",
        .root = "src/services/screening/main.zig",
        .target = target,
        .optimize = optimize,
        .libs = lib_deps,
    });

    // Scoring service
    const scoring = addService(b, .{
        .name = "scoring",
        .root = "src/services/scoring/main.zig",
        .target = target,
        .optimize = optimize,
        .libs = lib_deps,
    });

    // Unit tests
    const test_step = b.step("test", "Run unit tests");

    const test_module = b.createModule(.{
        .root_source_file = b.path("src/lib/root.zig"),
        .target = target,
        .optimize = optimize,
        .link_libc = true,
    });
    const lib_tests = b.addTest(.{
        .root_module = test_module,
    });
    const run_lib_tests = b.addRunArtifact(lib_tests);
    test_step.dependOn(&run_lib_tests.step);

    // Install all services
    b.installArtifact(gateway);
    b.installArtifact(screening);
    b.installArtifact(scoring);
}

const ServiceConfig = struct {
    name: []const u8,
    root: []const u8,
    target: std.Build.ResolvedTarget,
    optimize: std.builtin.OptimizeMode,
    libs: Libs,

    const Libs = struct {
        pq: []const u8,
        hiredis: []const u8,
        protobuf_c: []const u8,
    };
};

fn addService(b: *std.Build, config: ServiceConfig) *std.Build.Step.Compile {
    // Create module first
    const root_module = b.createModule(.{
        .root_source_file = b.path(config.root),
        .target = config.target,
        .optimize = config.optimize,
        .link_libc = true,
    });

    const exe = b.addExecutable(.{
        .name = config.name,
        .root_module = root_module,
    });

    // Link C libraries
    exe.linkSystemLibrary(config.libs.pq);
    exe.linkSystemLibrary(config.libs.hiredis);
    exe.linkSystemLibrary(config.libs.protobuf_c);

    // Add include paths for generated protobuf-c headers
    exe.addIncludePath(b.path("gen/c"));

    // Add zap dependency
    const zap = b.dependency("zap", .{
        .target = config.target,
        .optimize = config.optimize,
    });
    root_module.addImport("zap", zap.module("zap"));

    return exe;
}

