const std = @import("std");
const zap = @import("zap");

const log = std.log.scoped(.gateway);

pub fn main() !void {
    var gpa = std.heap.GeneralPurposeAllocator(.{}){};
    defer _ = gpa.deinit();

    const config = try loadConfig();

    log.info("starting gateway on port {d}", .{config.port});

    var listener = zap.HttpListener.init(.{
        .port = config.port,
        .on_request = handleRequest,
        .log = true,
    });

    listener.listen() catch |err| {
        log.err("failed to start listener: {}", .{err});
        return err;
    };

    log.info("gateway listening on :{d}", .{config.port});

    // Start the event loop (single-threaded for now)
    zap.start(.{ .threads = 1, .workers = 1 });
}

fn handleRequest(req: zap.Request) anyerror!void {
    const path = req.path orelse "/";

    if (std.mem.eql(u8, path, "/health")) {
        handleHealth(req);
    } else if (std.mem.eql(u8, path, "/sentinel.v1.GatewayService/ScreenTransaction")) {
        handleScreenTransaction(req);
    } else if (std.mem.eql(u8, path, "/sentinel.v1.GatewayService/Health")) {
        handleHealth(req);
    } else {
        sendConnectError(req, .not_found, "endpoint not found");
    }
}

fn handleHealth(req: zap.Request) void {
    req.setStatus(.ok);
    req.setHeader("content-type", "application/json") catch {};
    req.sendBody(
        \\{"healthy":true,"version":"0.1.0","dependencies":{}}
    ) catch {};
}

fn handleScreenTransaction(req: zap.Request) void {
    // Validate Connect protocol version
    const protocol_version = req.getHeader("connect-protocol-version") orelse {
        return sendConnectError(req, .invalid_argument, "missing Connect-Protocol-Version");
    };
    _ = protocol_version;

    // Validate content type
    const content_type_raw = req.getHeader("content-type") orelse {
        return sendConnectError(req, .invalid_argument, "missing content-type");
    };
    const content_type = parseMediaType(content_type_raw);

    if (std.mem.eql(u8, content_type, "application/json")) {
        // TODO: Parse JSON request, process, return JSON response
        sendConnectError(req, .unimplemented, "ScreenTransaction not implemented");
    } else if (std.mem.eql(u8, content_type, "application/proto")) {
        // TODO: Parse protobuf request, process, return protobuf response
        sendConnectError(req, .unimplemented, "ScreenTransaction not implemented");
    } else {
        sendConnectError(req, .invalid_argument, "unsupported content-type");
    }
}

fn parseMediaType(raw: []const u8) []const u8 {
    return if (std.mem.indexOf(u8, raw, ";")) |idx| raw[0..idx] else raw;
}

const ConnectErrorCode = enum {
    canceled,
    invalid_argument,
    not_found,
    permission_denied,
    internal,
    unavailable,
    unimplemented,
};

fn sendConnectError(req: zap.Request, code: ConnectErrorCode, msg: []const u8) void {
    const status: zap.http.StatusCode = switch (code) {
        .canceled => .request_timeout,
        .invalid_argument => .bad_request,
        .not_found => .not_found,
        .permission_denied => .forbidden,
        .internal => .internal_server_error,
        .unavailable => .service_unavailable,
        .unimplemented => .not_implemented,
    };

    const code_str = switch (code) {
        .canceled => "canceled",
        .invalid_argument => "invalid_argument",
        .not_found => "not_found",
        .permission_denied => "permission_denied",
        .internal => "internal",
        .unavailable => "unavailable",
        .unimplemented => "unimplemented",
    };

    req.setStatus(status);
    req.setHeader("content-type", "application/json") catch {};

    var buf: [256]u8 = undefined;
    const body = std.fmt.bufPrint(&buf, "{{\"code\":\"{s}\",\"message\":\"{s}\"}}", .{ code_str, msg }) catch {
        req.sendBody("{\"code\":\"internal\",\"message\":\"error formatting response\"}") catch {};
        return;
    };
    req.sendBody(body) catch {};
}

const Config = struct {
    port: u16,
    database_url: []const u8,
    redis_url: []const u8,
};

fn loadConfig() !Config {
    return .{
        .port = blk: {
            const port_str = std.posix.getenv("PORT") orelse "8080";
            break :blk std.fmt.parseInt(u16, port_str, 10) catch 8080;
        },
        .database_url = std.posix.getenv("DATABASE_URL") orelse "postgresql://localhost/sentinel",
        .redis_url = std.posix.getenv("REDIS_URL") orelse "redis://localhost:6379",
    };
}

