-- Sentinel Initial Schema
-- Phase 1: Core entities for compliance infrastructure

-- Enable required extensions
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
CREATE EXTENSION IF NOT EXISTS "pgcrypto";

-- Tenants table (multi-tenancy support)
CREATE TABLE tenants (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    name TEXT NOT NULL,
    slug TEXT NOT NULL UNIQUE,
    status TEXT NOT NULL DEFAULT 'active' CHECK (status IN ('active', 'suspended', 'deactivated')),
    config JSONB NOT NULL DEFAULT '{}',
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW()
);

CREATE INDEX idx_tenants_slug ON tenants(slug);
CREATE INDEX idx_tenants_status ON tenants(status);

-- Customers table (KYC subjects)
CREATE TABLE customers (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    tenant_id UUID NOT NULL REFERENCES tenants(id),
    external_id TEXT NOT NULL,
    risk_tier TEXT NOT NULL DEFAULT 'low' CHECK (risk_tier IN ('low', 'medium', 'high')),
    kyc_status TEXT NOT NULL DEFAULT 'pending' CHECK (kyc_status IN ('pending', 'approved', 'rejected', 'expired')),
    cdd_level TEXT NOT NULL DEFAULT 'standard' CHECK (cdd_level IN ('simplified', 'standard', 'enhanced')),
    risk_score INTEGER CHECK (risk_score >= 0 AND risk_score <= 100),
    pep_status BOOLEAN NOT NULL DEFAULT FALSE,
    sanctions_match BOOLEAN NOT NULL DEFAULT FALSE,
    last_kyc_check TIMESTAMPTZ,
    metadata JSONB NOT NULL DEFAULT '{}',
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    UNIQUE(tenant_id, external_id)
);

CREATE INDEX idx_customers_tenant_id ON customers(tenant_id);
CREATE INDEX idx_customers_external_id ON customers(tenant_id, external_id);
CREATE INDEX idx_customers_risk_tier ON customers(tenant_id, risk_tier);
CREATE INDEX idx_customers_kyc_status ON customers(tenant_id, kyc_status);

-- Transactions table (transaction monitoring)
CREATE TABLE transactions (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    tenant_id UUID NOT NULL REFERENCES tenants(id),
    tx_hash TEXT NOT NULL,
    chain TEXT NOT NULL,
    from_address TEXT NOT NULL,
    to_address TEXT NOT NULL,
    amount NUMERIC NOT NULL,
    asset TEXT NOT NULL,
    customer_id UUID REFERENCES customers(id),
    risk_score INTEGER CHECK (risk_score >= 0 AND risk_score <= 100),
    screening_result TEXT CHECK (screening_result IN ('pass', 'block', 'review', 'pending')),
    screening_latency_ms INTEGER,
    metadata JSONB NOT NULL DEFAULT '{}',
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
    UNIQUE(tenant_id, chain, tx_hash)
);

CREATE INDEX idx_transactions_tenant_id ON transactions(tenant_id);
CREATE INDEX idx_transactions_customer_id ON transactions(customer_id);
CREATE INDEX idx_transactions_chain_hash ON transactions(chain, tx_hash);
CREATE INDEX idx_transactions_from_address ON transactions(from_address);
CREATE INDEX idx_transactions_to_address ON transactions(to_address);
CREATE INDEX idx_transactions_created_at ON transactions(tenant_id, created_at DESC);
CREATE INDEX idx_transactions_screening_result ON transactions(tenant_id, screening_result);

-- Screening results table (sanctions/wallet risk checks)
CREATE TABLE screening_results (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    tenant_id UUID NOT NULL REFERENCES tenants(id),
    entity_type TEXT NOT NULL CHECK (entity_type IN ('customer', 'wallet', 'transaction')),
    entity_id UUID NOT NULL,
    check_type TEXT NOT NULL CHECK (check_type IN ('sanctions', 'pep', 'adverse_media', 'wallet_risk')),
    result TEXT NOT NULL CHECK (result IN ('clear', 'match', 'potential_match', 'error')),
    score INTEGER CHECK (score >= 0 AND score <= 100),
    match_details JSONB,
    vendor TEXT,
    vendor_response JSONB,
    expires_at TIMESTAMPTZ,
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW()
);

CREATE INDEX idx_screening_results_tenant_id ON screening_results(tenant_id);
CREATE INDEX idx_screening_results_entity ON screening_results(entity_type, entity_id);
CREATE INDEX idx_screening_results_check_type ON screening_results(tenant_id, check_type);
CREATE INDEX idx_screening_results_result ON screening_results(tenant_id, result);
CREATE INDEX idx_screening_results_created_at ON screening_results(tenant_id, created_at DESC);

-- Audit log (immutable compliance trail)
CREATE TABLE audit_log (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    tenant_id UUID NOT NULL REFERENCES tenants(id),
    actor_id TEXT NOT NULL,
    actor_role TEXT NOT NULL,
    action TEXT NOT NULL,
    entity_type TEXT NOT NULL,
    entity_id UUID,
    old_values JSONB,
    new_values JSONB,
    correlation_id UUID,
    ip_address INET,
    user_agent TEXT,
    created_at TIMESTAMPTZ NOT NULL DEFAULT NOW()
);

-- Audit log is append-only - no updates or deletes allowed
CREATE INDEX idx_audit_log_tenant_id ON audit_log(tenant_id);
CREATE INDEX idx_audit_log_entity ON audit_log(entity_type, entity_id);
CREATE INDEX idx_audit_log_actor ON audit_log(tenant_id, actor_id);
CREATE INDEX idx_audit_log_action ON audit_log(tenant_id, action);
CREATE INDEX idx_audit_log_created_at ON audit_log(tenant_id, created_at DESC);
CREATE INDEX idx_audit_log_correlation ON audit_log(correlation_id);

-- Updated_at trigger function
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = NOW();
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Apply updated_at triggers
CREATE TRIGGER update_tenants_updated_at
    BEFORE UPDATE ON tenants
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_customers_updated_at
    BEFORE UPDATE ON customers
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_transactions_updated_at
    BEFORE UPDATE ON transactions
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

-- Comments for documentation
COMMENT ON TABLE tenants IS 'Multi-tenant organization registry';
COMMENT ON TABLE customers IS 'KYC subjects with risk assessment';
COMMENT ON TABLE transactions IS 'Monitored transactions with screening results';
COMMENT ON TABLE screening_results IS 'Sanctions, PEP, and wallet risk check results';
COMMENT ON TABLE audit_log IS 'Immutable compliance audit trail (5-year retention)';

