// Connect protocol module
//
// Helpers for implementing Connect protocol handlers in Zig.
// See: https://connectrpc.com/docs/protocol

const std = @import("std");

const log = std.log.scoped(.connect);

pub const ErrorCode = enum {
    canceled,
    unknown,
    invalid_argument,
    deadline_exceeded,
    not_found,
    already_exists,
    permission_denied,
    resource_exhausted,
    failed_precondition,
    aborted,
    out_of_range,
    unimplemented,
    internal,
    unavailable,
    data_loss,
    unauthenticated,
};

pub const Error = struct {
    code: ErrorCode,
    message: []const u8,
};

/// Parse media type from Content-Type header, stripping charset and parameters.
/// e.g., "application/json; charset=utf-8" -> "application/json"
pub fn parseMediaType(raw: []const u8) []const u8 {
    return if (std.mem.indexOf(u8, raw, ";")) |idx| raw[0..idx] else raw;
}

/// Map Connect error code to HTTP status code.
pub fn errorCodeToHttpStatus(code: ErrorCode) u16 {
    return switch (code) {
        .canceled => 408,
        .unknown => 500,
        .invalid_argument => 400,
