// Configuration module
//
// Loads configuration from environment variables with validation.

const std = @import("std");

const log = std.log.scoped(.config);

pub const ConfigError = error{
    MissingRequired,
    InvalidFormat,
};

pub const ServiceConfig = struct {
    port: u16,
    database_url: []const u8,
    redis_url: []const u8,
    log_level: std.log.Level,
};

pub fn load() ConfigError!ServiceConfig {
    const port_str = std.posix.getenv("PORT") orelse "8080";
    const port = std.fmt.parseInt(u16, port_str, 10) catch return error.InvalidFormat;

    return .{
        .port = port,
        .database_url = std.posix.getenv("DATABASE_URL") orelse return error.MissingRequired,
        .redis_url = std.posix.getenv("REDIS_URL") orelse "redis://localhost:6379",
        .log_level = parseLogLevel(std.posix.getenv("LOG_LEVEL") orelse "info"),
    };
}

fn parseLogLevel(level: []const u8) std.log.Level {
    if (std.mem.eql(u8, level, "debug")) return .debug;
    if (std.mem.eql(u8, level, "info")) return .info;
    if (std.mem.eql(u8, level, "warn")) return .warn;
    if (std.mem.eql(u8, level, "err")) return .err;
    return .info;
}

test "parseLogLevel" {
    try std.testing.expectEqual(std.log.Level.debug, parseLogLevel("debug"));
    try std.testing.expectEqual(std.log.Level.info, parseLogLevel("info"));
    try std.testing.expectEqual(std.log.Level.warn, parseLogLevel("warn"));
    try std.testing.expectEqual(std.log.Level.err, parseLogLevel("err"));
    try std.testing.expectEqual(std.log.Level.info, parseLogLevel("unknown"));
}

