test "PageList resize reflow grapheme map capacity exceeded" {
    // This test verifies that when reflowing content with many graphemes,
    // the grapheme map capacity is correctly increased when needed.
    const testing = std.testing;
    const alloc = testing.allocator;

    var s = try init(alloc, 4, 10, 0);
    defer s.deinit();
    try testing.expectEqual(@as(usize, 1), s.totalPages());

    // Get the grapheme capacity from the page. We need more than this many
    // graphemes in a single destination page to trigger capacity increase
    // during reflow. Since each source page can only hold this many graphemes,
    // we create two source pages with graphemes that will merge into one
    // destination page.
    const grapheme_capacity = s.pages.first.?.data.graphemeCapacity();
    // Use slightly more than half the capacity per page, so combined they
    // exceed the capacity of a single destination page.
    const graphemes_per_page = grapheme_capacity / 2 + grapheme_capacity / 4;

    // Grow to the capacity of the first page and add more rows
    // so that we have two pages total.
    {
        const page = &s.pages.first.?.data;
        page.pauseIntegrityChecks(true);
        for (page.size.rows..page.capacity.rows) |_| {
            _ = try s.grow();
        }
        page.pauseIntegrityChecks(false);
        try testing.expectEqual(@as(usize, 1), s.totalPages());
        try s.growRows(graphemes_per_page);
        try testing.expectEqual(@as(usize, 2), s.totalPages());

        // We now have two pages.
        try testing.expect(s.pages.first.? != s.pages.last.?);
        try testing.expectEqual(s.pages.last.?, s.pages.first.?.next);
    }

    // Add graphemes to both pages. We add graphemes to rows at the END of the
    // first page, and graphemes to rows at the START of the second page.
    // When reflowing to 2 columns, these rows will wrap and stay together
    // on the same destination page, requiring capacity increase.

    // Add graphemes to the end of the first page (last rows)
    {
        const page = &s.pages.first.?.data;
        const start_row = page.size.rows - graphemes_per_page;
        for (0..graphemes_per_page) |i| {
            const y = start_row + i;
            const rac = page.getRowAndCell(0, y);
            rac.cell.* = .{
                .content_tag = .codepoint,
                .content = .{ .codepoint = 'A' },
            };
            try page.appendGrapheme(rac.row, rac.cell, @as(u21, @intCast(0x0301)));
        }
    }

    // Add graphemes to the beginning of the second page
    {
        const page = &s.pages.last.?.data;
        const count = @min(graphemes_per_page, page.size.rows);
        for (0..count) |y| {
            const rac = page.getRowAndCell(0, y);
            rac.cell.* = .{
                .content_tag = .codepoint,
                .content = .{ .codepoint = 'B' },
            };
            try page.appendGrapheme(rac.row, rac.cell, @as(u21, @intCast(0x0302)));
        }
    }

    // Resize to fewer columns to trigger reflow.
    // The graphemes from both pages will be copied to destination pages.
    // They will all end up in a contiguous region of the destination.
    // If the bug exists (hyperlink_bytes increased instead of grapheme_bytes),
    // this will fail with GraphemeMapOutOfMemory when we exceed capacity.
    try s.resize(.{ .cols = 2, .reflow = true });

    // Verify the resize succeeded
    try testing.expectEqual(@as(usize, 2), s.cols);
}

test "PageList resize (no reflow) shrink rows clamps viewport pin" {
    // Regression test for viewport overflow crash.
    // When rows shrink, the viewport pin can end up positioned such that
    // pin.y + rows > total_rows, causing getBottomRight(.viewport) to panic.
    //
    // Scenario from crash report:
    //   rows=76, total_rows=116, viewport_pin.y=46
    //   active_top = 116 - 76 = 40
    //   bottom_target = 46 + 75 = 121 > 115 (max row index)
    //
    // The fix: after shrinking rows, clamp viewport to .active if the pin
    // cannot fit the viewport height.
    const testing = std.testing;
    const alloc = testing.allocator;

    // Create a PageList: 10 cols, 10 rows, with max_scrollback so we can grow
    var s = try init(alloc, 10, 10, null);
    defer s.deinit();

    // Grow to create scrollback (30 total rows, 10 visible = 20 in scrollback)
    for (0..20) |_| _ = try s.grow();
    try testing.expectEqual(@as(usize, 10), s.rows);
    try testing.expectEqual(@as(usize, 30), s.totalRows());

    // Scroll up to pin the viewport at row 25.
    // Active starts at row 20 (30 - 10), so row 25 is in the active area.
    // But scroll will set viewport to .active if pinIsActive, so we need
    // to pin to row 19 which is in history, then grow will make it active.
    //
    // Actually, let's pin at row 15 (in history), then shrink rows so that
    // the pin ends up needing more rows than available below it.
    s.scroll(.{ .row = 15 });
    try testing.expectEqual(Viewport.pin, s.viewport);

    // Shrink from 10 rows to 20 rows... wait, that's growing.
    // We need a different approach:
    //
    // Pin at row 25, then shrink rows. But scroll(.row = 25) will hit active
    // (since active_top = 20) and set viewport to .active.
    //
    // New approach: pin in history, then shrink rows such that the pin
    // cannot fit the new (smaller) row count below it.
    //
    // Current state: total=30, rows=10, pinned at row 15 (in history)
    // After shrink to rows=5: active_top = 30 - 5 = 25
    //   Pin at row 15 is in history (15 < 25)
    //   Need to reach row 15 + 4 = 19, which exists. No crash.
    //
    // Need: pin close to end of content, then shrink.
    // Reset and try again with pin closer to end.

    // Pin at row 25 - but we can't because scroll will clamp to active.
    // So we need to manipulate viewport_pin directly for the test.
    // This simulates the state after a reflow where the pin is remapped
    // but not validated.
    s.viewport_pin.* = .{
        .node = s.pages.last.?,
        .y = 25,
    };
    s.viewport = .pin;

    // Current state:
    //   total_rows = 30, rows = 10, viewport_pin.y = 25
    //   active_top = 30 - 10 = 20
    //   Pin at 25 is inside active (25 >= 20)
    //   getBottomRight needs row 25 + 9 = 34, but max is 29 -> would crash!

    // But wait, we need to trigger via resize. Let's verify the setup first.
    // At this point getBottomRight would already crash. The test is:
    // after resize (shrink), the viewport should be clamped.

    // Shrink rows from 10 to 8
    try s.resize(.{ .rows = 8, .reflow = false });

    // After shrink:
    //   total_rows = 30 (unchanged, no blank rows to trim)
    //   rows = 8
    //   active_top = 30 - 8 = 22
    //   viewport_pin.y = 25 (still >= 22, inside active)
    //   Need row 25 + 7 = 32, but max is 29 -> overflow!
    //
    // The fix should clamp viewport to .active when this happens.
    try testing.expectEqual(Viewport.active, s.viewport);

    // Verify getBottomRight doesn't panic
    const br = s.getBottomRight(.viewport);
    try testing.expect(br != null);
}
